                         },
          'INSTALLDIRS' => 'site',
          'EXE_FILES' => [],
          'PL_FILES' => {},
          'realclean'  => {FILES=> join ' ', @files},
          # In the core pods will be built by installman.
          $core ? (MAN3PODS => {}) : (),
        )
;
